
public class vowel2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name="dipika murudkar";
		char ch;
		int count=0;
		for(int i=0;i<name.length();i++) {
			ch=name.charAt(i);
			if(ch=='a' || ch=='e'||ch=='i'||ch=='o'||ch=='u') {
				count++;
			}
		}
	  System.out.println(count);
	}

}
